import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Header from './Header/Headercomponent.js';
import Body from './Body/Bodycomponent.js';
import WeatherList from './WeatherComponents/WeatherList';
import CustomSpinner from './CustomSpinner.js';
class App extends Component {

constructor(){
  super();
  this.state = {
    isLoadingData : false,
    consolidated_weather : [],
    overskrift: "Velkommen!",
    introTekst: "Her kommer været"
  }
}

componentDidMount(){
  this.getDataFromAPI();
}

  render() {
    return (
      <div className="App">
        <Header tekst={this.state.overskrift}/> 
        <Body tekst={this.state.introTekst}/>

        <WeatherList results={this.state.consolidated_weather}/>

        <CustomSpinner loading={this.state.isLoadingData} ></CustomSpinner>
      </div>
    );
  }

  updateStates = (data) => {
    this.setState({
      consolidated_weather : data.consolidated_weather,
      isLoadingData : false
    });
   }

  getDataFromAPI = () => {
    fetch('https://www.metaweather.com/api/location/862592/', this.setState({isLoadingData : true}))
    .then(res => res.json())
    .then(json => {
      console.log("Success");
      this.updateStates(json);
    });
  }



}

export default App;
