import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Person from './Person/Person.js';

class App extends Component {
state = {
  persons: [
      { name: 'Manni', age: 28},
      { name: 'Harambe', age: 99}
  ]
}

switchNameHandler = () => {
  console.log('Was clicked');
  this.setState({
    persons: [
      { name: 'Manveer', age: 123},
      { name: 'HarambeIsBack', age: 18}
    ]
  })
}

  render() {
    return (
      <div className="App">

        <h1> Hi, im a react app </h1>
        <p> Testing </p>
        <button onClick ={this.switchNameHandler}  > Click me to change name! </button>
         <Person name={this.state.persons[1].name} age={this.state.persons[1].age} />
      </div>
    );
  }
}

export default App;
