import logo from './logo.svg';
import './App.css';
import Header from './Header/Headercomponent.js';
import Body from './Body/Bodycomponent.js';
import WeatherList from './WeatherComponents/WeatherList';
class App extends Component {

constructor(){
  super();
  this.state = {
    location_type : " Test",
  }
}

componentDidMount(){
  this.getDataFromAPI();
}

  render() {
    return (
      <div className="App">
        <Header tekst={this.state.location_type}/> 
        <Body tekst="Her kommer vÃ¦ret"/>
        <WeatherList/>
      </div>
    );
  }

  updateStates = (data) => {
    this.setState({
      location_type : data[0].location_type
    });
   }

  getDataFromAPI = () => {
    fetch('https://www.metaweather.com/api/location/search/?query=oslo', {
      method: 'get',
      headers: {'Content-Type': 'application/json'},
  }).then(response => response.json())
      .then(responseJson => {
         this.updateStates(responseJson)
      })
      .catch(response => {
          this.setState({accepted: false, clicked: false, loading: false});
          return response;
      });
  }




}

export default App;
